import React, {useEffect} from "react";
import {nanoid} from "nanoid"
import Question from "./Question";

// https://opentdb.com/api.php?amount=6&category=18&difficulty=easy
// https://opentdb.com/api.php?amount=5
export default function Quiz() {
    // const [begin, setBegin] = React.useState(false)
    const [count, setCount] = React.useState(0)
    const [quiz, setQuiz] = React.useState([{
        question: "",
        answers: [],
        correctAnswer: "",
        selectedAnswer: "",
        id: "",
        score: null
    }])

    const [currentQuestion, setCurrentQuestion] = React.useState(0)
    const [submitAnswer, setSubmitAnswer] = React.useState(false)

    useEffect(() => {
        fetch("https://opentdb.com/api.php?amount=2")
            .then(res => res.json())
            .then(data => {
                setQuiz(data.results.map(item => ({
                    question: item.question.replace(/&quot;/g, '"').replace(/&#039;/g, '"'),
                    answers: [item.correct_answer, ...item.incorrect_answers]
                        .sort(() => Math.random() - 0.5),
                    correctAnswer: item.correct_answer,
                    selectedAnswer: "",
                    id: nanoid()
                })))
            })
    }, [count])

    function handleClick(value, questionId){
        setQuiz(prev => {
            return prev.map(que => {
                return questionId === que.id? {...que, selectedAnswer: `${value}`} : {...que}
            })
        })
    }

    console.log(quiz)

    function checkAnswer(){
        setSubmitAnswer(true)
        let num = 0
        quiz.map(que => {
            if(que.correctAnswer === que.selectedAnswer){
                num++
            }
        })
        alert(num)
    }

    const questions = quiz.map((que) => {
        return <Question
            key = {que.id}
            {...que}
            handleClick={handleClick}
            />
    })

    return (
        <div className="questions">
            {questions}
            <button onClick={checkAnswer}>Check Answer</button>
            <p>{submitAnswer?"1":"2"}</p>
        </div>

    )
}
