import React from "react";

export default function Question(props) {
    // let test = []
    // console.log(answers.length)
    // test.map(answer => {
    //     return (<p className="single-answer">1</p>)
    // })

    const myAnswers = props.answers.map(ans => {
        return <p>{ans}</p>
    })
    return (
        <div>
            <h2>{props.question}</h2>
            {myAnswers}
        </div>
    )
}
