import React from "react";

export default function Question(props) {

    const [on, setOn] = React.useState(false)
    const [currentValue, setCurrentValue] = React.useState("")

    function handleChange(event){
        const {value, id} = event.target
        props.handleClick(value, id)
        setCurrentValue(value)
        setOn(true)
    }

    function checkIsSelected(value){
        return on && value === currentValue? "selected_btn" :"form_btn"
    }

    function checkIsCorrect(value){
        let message = ""
        if(props.disability){
            if(value === currentValue || value==""){
                message = "red_btn"
            }
            if(value === props.correctAnswer){
                message = "green_btn"
            }
        }
        return message
        // return (props.disability) && (value === currentValue? "blue_btn" : "green_btn")
    }

    const myAnswers = props.answers.map(ans => {
        ans = ans.replace(/&quot;/g, '"').replace(/&#039;/g, '"')
        return (
            <button
                className={`form_btn ${checkIsSelected(ans)} ${checkIsCorrect(ans)}`}
                key = {ans}
                value={ans}
                id={props.id}
                onClick={handleChange}
            >
                {ans}
            </button>
        )
    })

    return (
        <div>
            <h3>{props.question}</h3>
            {myAnswers}
            <hr />
        </div>
    )
}
